'use strict';

var grunt = require('grunt');

/*
  ======== A Handy Little Nodeunit Reference ========
  https://github.com/caolan/nodeunit

  Test methods:
    test.expect(numAssertions)
    test.done()
  Test assertions:
    test.ok(value, [message])
    test.equal(actual, expected, [message])
    test.notEqual(actual, expected, [message])
    test.deepEqual(actual, expected, [message])
    test.notDeepEqual(actual, expected, [message])
    test.strictEqual(actual, expected, [message])
    test.notStrictEqual(actual, expected, [message])
    test.throws(block, [error], [message])
    test.doesNotThrow(block, [error], [message])
    test.ifError(value)
*/

exports.json_to_object = {
  setUp: function(done) {
    // setup here if necessary
    done();
  },
  iso_langs: function(test) {
    test.expect(1);

    var actual = grunt.file.read('tmp/iso_langs.js');
    var expected = grunt.file.read('test/expected/iso_langs.js');
    test.equal(actual, expected, 'should be a file containing a Javascript object.');

    test.done();
  } ,
  iso_langs_split: function(test) {
    test.expect(1);

    var actual = grunt.file.read('tmp/iso_langs_split.js');
    var expected = grunt.file.read('test/expected/iso_langs_split.js');
    test.equal(actual, expected, 'should be a Javascript object containing two attributes.');

    test.done();
  }
};
